def validar_mision(globals_dict):
    try:
        if 'respuesta' not in globals_dict:
            return False, "El Golem sigue en silencio. Debes definir la variable 'respuesta'."

        res = globals_dict['respuesta']
        if not isinstance(res, str):
            return False, "La respuesta del Golem debe ser texto."

        if "acordarme" not in res and "hobbit" not in res:
            return False, "El Golem no ha completado la frase correctamente. ¿Usaste la función 'generar'?"

        return True, "¡Ha despertado! El Golem respira. Ahora, aprendamos cómo funciona su mente."
    except Exception as e:
        return False, f"Error en el despertar: {str(e)}"
