def validar_mision(globals_dict):
    try:
        if 'codificador' not in globals_dict:
            return False, "¡El codificador no ha sido invocado! Define la variable 'codificador'."
        if 'tokens_alma' not in globals_dict:
            return False, "¡La esencia no ha sido extraída! Define la variable 'tokens_alma'."

        cod = globals_dict['codificador']
        toks = globals_dict['tokens_alma']

        # Check if it's the mock encoding object
        if not hasattr(cod, 'encode') or not hasattr(cod, 'name'):
             return False, "El objeto 'codificador' parece incorrecto."

        if not isinstance(toks, list):
            return False, "La variable 'tokens_alma' debe ser una lista de números."

        # Expected Logic
        import tiktoken
        truth_enc = tiktoken.get_encoding("cl100k_base")
        expected_toks = truth_enc.encode("El conocimiento es poder, pero la sabiduría es libertad.")

        if toks != expected_toks:
            return False, f"La transmutación es incorrecta. Se esperaban {len(expected_toks)} fragmentos. ¿Usaste el pergamino correcto?"

        return True, "¡Transmutación exitosa! El Golem absorbe los fragmentos con avidez."
    except Exception as e:
        return False, f"Error en la validación: {str(e)}"
