def validar_mision(globals_dict):
    try:
        if 'db' not in globals_dict:
            return False, "¡El Archivo no existe! Inicializa la base de datos 'db'."

        db_inst = globals_dict['db']
        if not hasattr(db_inst, 'storage') or not db_inst.storage:
            return False, "El Archivo está vacío. Debes insertar al menos un recuerdo."

        # Check content
        found_metadata = False
        for k, v in db_inst.storage.items():
            if 'meta' in v and isinstance(v['meta'], dict) and len(v['meta']) > 0:
                found_metadata = True
                break

        if not found_metadata:
            return False, "Los recuerdos son inútiles sin contexto. ¡Añade un diccionario de 'metadata' al insertar!"

        if 'resultados' not in globals_dict or not globals_dict['resultados']:
            return False, "Debes probar el sistema. Ejecuta una `query` y guarda el resultado en la variable 'resultados'."

        return True, "¡Archivado Completo! El Golem ahora tiene una memoria perfecta y organizada."
    except Exception as e:
        return False, f"Error en el sistema de archivos: {str(e)}"
