def validar_mision(globals_dict):
    try:
        if 'momento_clave' not in globals_dict:
            return False, "No has definido la variable 'momento_clave'."

        t = globals_dict['momento_clave']

        if t == -1:
            return False, "El valor sigue siendo -1. Tu bucle no encontró nada."

        if t != 12.5:
            return False, f"Detuviste el tiempo en {t}s, pero ese no es el momento correcto. Busca 'llave'."

        return True, "¡Sincronización Temporal Perfecta! Has recuperado la llave y completado el Libro de los Sentidos."
    except Exception as e:
        return False, f"Error temporal: {str(e)}"
