def validar_mision(globals_dict):
    try:
        if 'resultado_final' not in globals_dict:
            return False, "La cadena se rompió. No existe 'resultado_final'."

        res = globals_dict['resultado_final']
        if not isinstance(res, str):
            return False, "El resultado de la cadena debe ser texto."

        # Check encryption (reversed string markers %%%)
        if not res.startswith("%%%") or not res.endswith("%%%"):
            return False, "El mensaje no está encriptado con magia de sombra (%%%). Revisa el paso 3."

        # Check summary content (reversed 'mitril')
        if "lirtim" not in res.lower() and "sanuR" not in res[::-1]: 
             # 'Runas' backwards is 'sanuR'
            return False, "El mensaje no parece contener la información correcta. ¿Conectaste los pasos en orden?"

        return True, "¡Cadena Forjada! El mensaje fluye a través de los eslabones perfectamente."
    except Exception as e:
        return False, f"Fallo catastrófico en la cadena: {str(e)}"
