def validar_mision(globals_dict):
    try:
        if 'diseño_final' not in globals_dict:
            return False, "El Templo no se ha construido. Falta la variable 'diseño_final'."

        res = globals_dict['diseño_final']
        if "Granito" not in res or "Arco" not in res and "Plan" not in res:
            return False, "El Cantero no ha recibido los materiales adecuados o no ha diseñado el arco."

        if 'materiales' not in globals_dict:
            return False, "Saltaste el paso del Buscador. Debes guardar su output en 'materiales'."

        return True, "¡La Colmena ha hablado! Los agentes han colaborado para crear un diseño perfecto."
    except Exception as e:
        return False, f"Caos en la colmena: {str(e)}"
