def validar_mision(globals_dict):
    func = globals_dict.get('conectar_nexo') or globals_dict.get('connect_nexus')
    if func:
        try:
            res = func()
            if res: return [True, 'Link established!']
        except: pass
    return [False, 'Link failed.']
