def interpretar_plano(documento_sdd):
    # Bilingual solution
    key_es = '## Esquema de Datos'
    key_en = '## Data Schema'
    if key_es in documento_sdd:
        return documento_sdd.split(key_es)[1]
    if key_en in documento_sdd:
        return documento_sdd.split(key_en)[1]
    return None

